mapper.select:
SELECT DISTINCT t.`lecture_pkg_id` as lecturePkgId, t.`lecture_id` as lectureId, t.`lecture_name` as lectureName, t.`lecture_summary` as lectureSummary, t.`class_id` AS classId FROM v_class_lesson_check t WHERE 1=1
AND t.`lecture_pkg_id`=${lecturePkgId}
AND t.`class_id`=${classId}
ORDER BY t.`lecture_id`

SELECT DISTINCT t.`lecture_id` AS lectureId , t.`lecture_name` AS lectureName, t.`lecture_summary` AS lectureSummary, t.`order_id` AS orderId, t.`task_pkg_id` AS taskPkgId, t.`task_pkg_name` AS taskPkgName, t.`summary` AS summary, t.`item_id` AS itemId, t.`item_name` AS itemName, t.`item_desc` AS itemDesc, t.`item_mark` AS itemMark, t.`class_id` AS classId, t.`lesson_id` AS lessonId FROM v_class_lesson_check t WHERE 1=1
AND t.`lecture_id`=${lectureId}
AND t.`class_id`=${classId}
ORDER BY t.`task_pkg_id`

SELECT COUNT(*) FROM t_study_lesson t WHERE 1=1

	AND t.`lecture_id`=${lectureId}


	AND t.`class_id`=${classId}

mapper.insert:
INSERT INTO t_study_lesson(lecture_id,teacher_id,class_id)
SELECT
DISTINCT t.`lecture_id`,t.`teacher_id`,t.`class_id` FROM
v_class_lesson_check t WHERE 1=1

	AND t.`lecture_id`=${lectureId}


	AND t.`class_id`=${classId}

mapper.select:
SELECT DISTINCT d.`customer_id` AS customerId, d.`customer_name` AS customerName, c.`student_id` AS studentId, (SELECT picture_addr FROM t_user_customer_pictures t WHERE t.customer_id=d.`customer_id` AND t.status=0) AS pictureAddr , b.`lesson_id` AS lessonId, a.`attend_id` AS attendId, a.`check_id` AS checkId, a.`check_status` AS checkStatus, a.`net_url` AS netUrl, a.`group_id` AS groupId, a.`seat_id` AS seatId FROM t_study_lesson_task_check a LEFT JOIN t_study_lesson_2_student b ON a.`attend_id`=b.`attend_id` LEFT JOIN t_user_student c ON b.`student_id`=c.`student_id` LEFT JOIN t_user_customer d ON c.`customer_id`=d.`customer_id` WHERE 1=1 AND a.`effect_status`=0
AND b.`lesson_id`=${lessonId}
AND a.task_pkg_id=${taskPkgId}
order by a.`group_id` , a.`seat_id`

SELECT a.`check_id` AS checkId, b.`task_pkg_id` AS taskPkgId, c.`lesson_id` AS lessonId, d.`student_id` AS studentId, e.`customer_id` AS customerId, e.`customer_name` AS customerName, g.`group_id` AS groupId, g.`seat_id` AS seatId FROM t_study_lesson_task_check a INNER JOIN t_study_task_package b ON a.`task_pkg_id`=b.`task_pkg_id` INNER JOIN t_study_lesson_2_student c ON a.`attend_id`=c.`attend_id` INNER JOIN t_user_student d ON c.`student_id`=d.`student_id` INNER JOIN t_user_customer e ON d.`customer_id`=e.`customer_id` INNER JOIN t_class_stu_2_class f ON d.`student_id`=f.`student_id` INNER JOIN t_class_seat g ON f.`id`=g.`stu_class_id` WHERE 1=1 AND c.`status` = 0
AND c.`lesson_id`=${lessonId}
AND b.`task_pkg_id`=${taskPkgId}
ORDER BY g.`group_id`, g.`seat_id`

SELECT a.`check_id` AS checkId, c.`picture_id` AS pictureId, c.`picture_url` AS pictureUrl, c.`check_status` AS checkStatus , f.`customer_name` AS customerName FROM t_study_lesson_task_check a INNER JOIN t_study_lesson_pictures b ON a.`check_id` = b.`check_id` INNER JOIN t_study_lesson_picture_detail c ON b.`pictures_id` = c.`pictures_id` INNER JOIN t_study_lesson_2_student d ON a.`attend_id`=d.`attend_id` INNER JOIN t_user_student e ON d.`student_id`=e.`student_id` INNER JOIN t_user_customer f ON e.`customer_id`=f.`customer_id` WHERE 1 = 1
AND a.`check_id`=${checkId}
AND a.`group_id`=${groupId}
AND a.`seat_id`=${seatId}
AND a.`create_time`=STR_TO_DATE('${uploadPicDate}', '%Y-%m-%d')

mapper.update:
UPDATE
t_study_lesson_picture_detail t SET t.`check_status`=${checkStatus}
WHERE picture_id=${pictureId}
